import { z } from 'zod';
import { phaseMap, type Phase } from '../workflows/phase-map.js';

const blueprintSchema = z.object({
  id: z.string(),
  name: z.string(),
  phase: z.custom<Phase>(),
  description: z.string(),
  acceptanceCriteria: z.string()
});

export type TaskBlueprint = z.infer<typeof blueprintSchema>;

export function createTaskBlueprint(input: TaskBlueprint): TaskBlueprint {
  blueprintSchema.parse(input);
  if (!phaseMap[input.phase]) {
    throw new Error(`Unknown phase ${input.phase}`);
  }
  return input;
}
EOF"`.
