import path from 'node:path';
import { pathToFileURL } from 'node:url';

export interface AgentDefinition {
  id: string;
  name: string;
  description: string;
  promptPath: string;
}

export async function loadAgents(baseDir: string): Promise<AgentDefinition[]> {
  const modulePath = path.join(baseDir, 'inputs', 'agents.js');
  const { default: agents = [] } = await import(pathToFileURL(modulePath).href);
  return (agents as AgentDefinition[]).map((agent) => ({
    ...agent,
    promptPath: path.resolve(baseDir, agent.promptPath)
  }));
}

export async function findAgent(id: string, baseDir: string): Promise<AgentDefinition | undefined> {
  const agents = await loadAgents(baseDir);
  return agents.find((agent) => agent.id === id);
}
