### Purpose
Encapsulate child_process spawning with promise-based API.

### Preconditions
- Node.js >= <NODE_VERSION>.

### Artifacts To Create/Modify
- `src/infra/process/spawn.ts`

### Step-by-Step
1. Run `bash -lc "set -euo pipefail; cat <<'EOF' > src/infra/process/spawn.ts
import { spawn } from 'node:child_process';

export interface SpawnOptions {
  command: string;
  args: string[];
  cwd?: string;
  env?: NodeJS.ProcessEnv;
}

export function spawnProcess(options: SpawnOptions): Promise<{ exitCode: number; stdout: string; stderr: string; }> {
  return new Promise((resolve, reject) => {
    const child = spawn(options.command, options.args, {
      cwd: options.cwd,
      env: options.env,
      stdio: ['ignore', 'pipe', 'pipe']
    });

    let stdout = '';
    let stderr = '';

    child.stdout?.on('data', (chunk) => {
      stdout += chunk.toString();
    });

    child.stderr?.on('data', (chunk) => {
      stderr += chunk.toString();
    });

    child.on('error', (error) => reject(error));
    child.on('close', (code) => resolve({ exitCode: code ?? 0, stdout, stderr }));
  });
}
EOF"`.
