### Step-by-Step
1. Run `bash -lc "set -euo pipefail; cat <<'EOF' > src/infra/codex/codex-runner.ts
import path from 'node:path';
import { spawnProcess } from '../process/spawn.js';

interface CodexRunnerOptions {
  profile: string;
  prompt: string;
  workingDir: string;
  onData?: (chunk: string) => void;
}

export async function runCodex(options: CodexRunnerOptions): Promise<string> {
  const args = [
    'exec',
    '--profile', options.profile,
    '--skip-git-repo-check',
    '--sandbox', 'danger-full-access',
    '--dangerously-bypass-approvals-and-sandbox',
    '-C', options.workingDir,
    options.prompt
  ];

  const result = await spawnProcess({
    command: path.join(process.env.CODEX_HOME ?? '', 'bin', 'codex'),
    args,
    cwd: options.workingDir,
    env: process.env
  });

  if (result.exitCode !== 0) {
    throw new Error(`Codex exited with code ${result.exitCode}: ${result.stderr}`);
  }

  if (options.onData) {
    options.onData(result.stdout);
  }

  return result.stdout;
}
EOF"`.
