import { promises as fs } from 'fs';
import path from 'node:path';
import { MemoryEntry } from '../../agents/memory/memory-store.js';

export class MemoryAdapter {
  constructor(private readonly baseDir: string) {}

  private fileFor(agentId: string): string {
    return path.join(this.baseDir, `${agentId}.json`);
  }

  async append(entry: MemoryEntry): Promise<void> {
    const file = this.fileFor(entry.agentId);
    const existing = await this.read(entry.agentId);
    existing.push(entry);
    await fs.mkdir(this.baseDir, { recursive: true });
    await fs.writeFile(file, JSON.stringify(existing, null, 2));
  }

  async read(agentId: string): Promise<MemoryEntry[]> {
    const file = this.fileFor(agentId);
    try {
      const json = await fs.readFile(file, 'utf8');
      return JSON.parse(json);
    } catch (error: any) {
      if (error.code === 'ENOENT') {
        return [];
      }
      throw error;
    }
  }
}
EOF"`.
